              data-testid="embed-iframe" 
              style={{borderRadius: '12px'}} 
              src="https://open.spotify.com/embed/playlist/3uJHpZiOHRFaj1VmDeZVQh?utm_source=generator&v=${Date.now()}" 
              width="300" 
              height="352" 
              frameBorder="0" 
              allowFullScreen="" 
              allow="autoplay; clipboard-write; encrypted-media; fullscreen; picture-in-picture" 
              loading="lazy"
              title="Spotify Playlist"
            ></iframe>
          </div>
        )}
      </div>

      <div className="terminal-content" ref={outputRef}>
        {output.map((item, index) => (
          <div key={index} className={`terminal-line ${item.type}`}>
            {item.content}
            {item.showCursor && (
              <span
                style={{
                  display: 'inline-block',
                  width: '8px',
                  height: '16px',
                  backgroundColor: '#fff',
                  marginLeft: '8px',
                  animation: 'blink 1s infinite',
                  verticalAlign: 'text-bottom',
                  position: 'relative'
                }}
              ></span>
            )}
          </div>
        ))}

        {!isTyping && (
          <div className="terminal-input-line">
            <span ref={promptRef} className="terminal-prompt">egirls@egirls.faith-MacBook-Pro ~ % </span>
            <input
              ref={inputRef}
              type="text"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyPress={handleKeyPress}
              className="terminal-input"
              placeholder=""
              disabled={isTyping}
            />
            {promptWidth > 0 && (
              <span
                className="terminal-cursor"
                style={{
                  position: 'absolute',
                  left: `${promptWidth + inputWidth + 2}px`,
                  top: '0'
                }}
              ></span>
            )}
          </div>
        )}
      </div>

      {/* Command Directory */}
      <div className="command-directory">
        <div className="command-directory-header">
          <span className="command-directory-title">Commands</span>
        </div>
        <div className="command-list">
          {commandDirectory.map((command, index) => (
            <div
              key={index}
              className="command-item"
              onClick={() => copyToClipboard(command, index)}
              title="Click to copy"
            >
              <span className="command-text">{command}</span>
              <span className="copy-hint">
                {copiedCommand === index ? 'copied!' : 'click to copy'}
              </span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Terminal;
