// Individual Photo Component
const PhotoItem = ({ photo, index, scrollYProgress }) => {
  // Randomized scale curves for each image
  const maxScale = 1.0 + Math.random() * 0.2; // 1.0 to 1.2
  const minScale = 0.4 + Math.random() * 0.2; // 0.4 to 0.6

  // Slight vertical offset for collage effect
  const verticalOffset = (Math.random() * 50) - 25; // ±25px up/down

  // Calculate initial horizontal position (spread across screen)
  const initialX = (index * 350) - (wineNightPhotos.length * 175); // Center the spread

  // Get current scroll progress
  const currentProgress = scrollYProgress.get ? scrollYProgress.get() : scrollYProgress;

  // Calculate horizontal translation based on scroll progress
  const translateX = initialX + (currentProgress * -1000); // Move left as scroll progresses

  // Calculate scale based on horizontal position (center = max scale)
  let scale = minScale;
  if (translateX > -200 && translateX < 200) {
    // In center area - interpolate between min and max scale
    const centerDistance = Math.abs(translateX);
    const scaleRatio = 1 - (centerDistance / 200);
    scale = minScale + (maxScale - minScale) * scaleRatio;
  } else if (translateX >= -200 && translateX <= 200) {
    scale = maxScale;
  }

  // Z-index based on scale for layering
  const zIndex = Math.floor(scale * 100);

  // Add console log to debug
  console.log(`Photo ${index}: translateX=${translateX}, scale=${scale}, zIndex=${zIndex}`);

  return (
    <motion.div
      className="photo-item"
      style={{
        transform: `translateX(${translateX}px) translateY(${verticalOffset}px) scale(${scale})`,
        zIndex,
        position: 'absolute',
        left: '50%', /* Center horizontally */
        top: '50%',  /* Center vertically */
        marginLeft: '-150px', /* Half of image width */
        marginTop: '-200px'   /* Half of image height */
      }}
      initial={{ opacity: 0 }}
      whileInView={{ opacity: 1 }}
      viewport={{ once: true, margin: "-10%" }}
      transition={{ duration: 0.6, ease: "easeOut" }}
    >
      <img 
        src={photo} 
        alt={`Wine night photo ${index + 1}`}
        onError={(e) => console.error(`Failed to load image: ${photo}`)}
        onLoad={() => console.log(`Successfully loaded image: ${photo}`)}
      />
    </motion.div>
  );
};

// Photo Timeline Component
const PhotoTimeline = () => {
  const containerRef = useRef(null);
  const [scrollProgress, setScrollProgress] = useState(0);

  // Use a simpler scroll detection method
  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      const windowHeight = window.innerHeight;
      const documentHeight = document.documentElement.scrollHeight;

      // Calculate scroll progress (0 to 1)
      const progress = scrollTop / (documentHeight - windowHeight);
      setScrollProgress(progress);

      console.log('Scroll detected:', { scrollTop, windowHeight, documentHeight, progress });
    };

    // Initial call to set initial scroll position
    handleScroll();

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Add console log to debug scroll progress
  console.log('Scroll Progress State:', scrollProgress);

  return (
    <div className="photo-timeline-container" ref={containerRef}>
      <div className="photo-timeline">
        {/* Debug info */}
        <div style={{ 
          position: 'fixed', 
          top: '100px', 
          left: '20px', 
          color: 'white', 
          zIndex: 9999,
          background: 'rgba(0,0,0,0.8)',
          padding: '10px',
          borderRadius: '5px'
        }}>
          <div>Scroll Progress: {scrollProgress.toFixed(3)}</div>
          <div>Photos Count: {wineNightPhotos.length}</div>
          <div>Container Ref: {containerRef.current ? 'Set' : 'Not Set'}</div>
          <div>Window Height: {window.innerHeight}</div>
          <div>Document Height: {document.documentElement.scrollHeight}</div>
          <div>Current Scroll: {window.pageYOffset || document.documentElement.scrollTop}</div>
        </div>

        {wineNightPhotos.map((photo, index) => (
          <PhotoItem 
            key={index}
            photo={photo}
            index={index}
            scrollYProgress={{ get: () => scrollProgress }}
          />
        ))}
      </div>
    </div>
  );
};

// Draggable Folder Component
const DraggableFolder = ({ name, initialX, initialY, onOpen }) => {
  const [position, setPosition] = useState({ x: initialX, y: initialY });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });

  const handleMouseDown = (e) => {
    e.preventDefault();
    setIsDragging(true);
    setDragStart({
      x: e.clientX - position.x,
      y: e.clientY - position.y
    });
  };

  const handleMouseMove = (e) => {
    if (isDragging) {
      setPosition({
        x: e.clientX - dragStart.x,
        y: e.clientY - dragStart.y
      });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleClick = (e) => {
    if (!isDragging) {
      onOpen(name);
    }
  };

  useEffect(() => {
    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isDragging, dragStart]);

  return (
    <div
      className="draggable-folder"
      style={{
        position: 'absolute',
        left: position.x,
        top: position.y,
        cursor: isDragging ? 'grabbing' : 'grab'
      }}
      onMouseDown={handleMouseDown}
      onClick={handleClick}
    >
      <img src="/macOSfolder.png" alt="folder" className="folder-icon" />
      <span className="folder-name">{name}</span>
    </div>
  );
};

const Terminal = ({ onClose }) => {
  const [inputValue, setInputValue] = useState('');
  const [output, setOutput] = useState([]);
  const [currentPage, setCurrentPage] = useState('terminal');
  const [isTyping, setIsTyping] = useState(false);
  const [copiedCommand, setCopiedCommand] = useState(null);
