import React, { useState, useEffect, useRef } from 'react';
import './Terminal.css';

const Terminal = ({ onClose }) => {
  const [inputValue, setInputValue] = useState('');
  const [output, setOutput] = useState([]);
  const [currentPage, setCurrentPage] = useState('terminal');
  const [isTyping, setIsTyping] = useState(false);
  const [copiedCommand, setCopiedCommand] = useState(null);
  const inputRef = useRef(null);
  const outputRef = useRef(null);

  const commands = {
    'help': 'Available commands:\n• open manifesto - View the manifesto\n• about - About page\n• help - Show this help\n• clear - Clear terminal\n• exit - Return to landing page',
    'about': 'Egirls is a digital art collective exploring the intersection of technology, identity, and expression. We create immersive experiences that challenge conventional web design paradigms.',
    'clear': 'clear',
    'exit': 'exit'
  };

  const manifestos = [
    'Digital Manifesto',
    '================',
    '',
    'We reject the tyranny of conventional web design.',
    'We embrace the terminal as our canvas.',
    'We believe in the power of minimalism.',
    'We believe in the power of minimalism.',
    'We create experiences that demand attention.',
    '',
    'In a world of infinite scroll and instant gratification,',
    'we choose to slow down, to zoom in, to explore.',
    'We believe that true connection comes from',
    'intentional interaction, not passive consumption.',
    '',
    'Our art is not just visual, it is experiential.',
    'Our code is not just functional, it is poetic.',
    'Our interface is not just usable, it is beautiful.',
    '',
    'Welcome to the future of web design.',
    'Welcome to Egirls.'
  ];

  const commandDirectory = [
    'cd about/',
    'cd photos/',
    'cd brand/',
    'cat manifesto.txt'
  ];

  useEffect(() => {
    // Add initial terminal output - only the login message, no prompt
    setOutput([
      { type: 'system', content: 'Last login: Tue Nov 11 11:11:11 on console' }
    ]);

    // Focus input on mount
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  useEffect(() => {
    // Auto-scroll to bottom when output changes
    if (outputRef.current) {
      outputRef.current.scrollTop = outputRef.current.scrollHeight;
    }
  }, [output]);

  const typeOutText = async (text, speed = 50) => {
    setIsTyping(true);
    const lines = text.split('\n');
    let currentOutput = [...output];

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];
      if (line === '') {
        currentOutput.push({ type: 'output', content: '' });
        setOutput([...currentOutput]);
        await new Promise(resolve => setTimeout(resolve, speed));
        continue;
      }

      let currentLine = '';
      for (let j = 0; j < line.length; j++) {
        currentLine += line[j];
        currentOutput[currentOutput.length - 1] = { type: 'output', content: currentLine };
        setOutput([...currentOutput]);
        await new Promise(resolve => setTimeout(resolve, speed));
      }

      if (i < lines.length - 1) {
        currentOutput.push({ type: 'output', content: '' });
        setOutput([...currentOutput]);
      }
    }

    // Add new prompt
    currentOutput.push({ type: 'system', content: 'egirls@egirls.faith-MacBook-Pro ~ % ' });
    setOutput([...currentOutput]);
    setIsTyping(false);
  };

  const handleCommand = async (command) => {
    const trimmedCommand = command.trim().toLowerCase();

    // Add command to output
    setOutput(prev => [...prev, { type: 'input', content: `egirls@egirls.faith-MacBook-Pro ~ % ${command}` }]);

    if (trimmedCommand === 'open manifesto') {
      setCurrentPage('manifesto');
      await typeOutText(manifestos.join('\n'));
    } else if (trimmedCommand === 'about') {
      setCurrentPage('about');
      await typeOutText(commands.about);
    } else if (trimmedCommand === 'help') {
      setCurrentPage('help');
      await typeOutText(commands.help);
    } else if (trimmedCommand === 'clear') {
      setOutput([
        { type: 'system', content: 'Last login: Tue Nov 11 11:11:11 on console' }
      ]);
    } else if (trimmedCommand === 'exit') {
      setCurrentPage('landing');
      // This would route back to landing page in a real app
      await typeOutText('Returning to landing page...');
    } else if (trimmedCommand !== '') {
      await typeOutText(`Command not found: ${command}. Type 'help' for available commands.`);
    }

    setInputValue('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !isTyping) {
      handleCommand(inputValue);
    }
  };

  const handleClose = () => {
    if (onClose) {
      onClose();
    }
  };

  const copyToClipboard = async (text, index) => {
    try {
      await navigator.clipboard.writeText(text);
      // Show visual feedback that command was copied
      setCopiedCommand(index);
      console.log('Command copied to clipboard:', text);

      // Reset the copied state after 2 seconds
      setTimeout(() => {
        setCopiedCommand(null);
      }, 2000);
    } catch (err) {
      // Fallback for older browsers
      const textArea = document.createElement('textarea');
      textArea.value = text;
      document.body.appendChild(textArea);
      textArea.select();
      document.execCommand('copy');
      document.body.removeChild(textArea);

      // Show visual feedback for fallback copy too
      setCopiedCommand(index);
      setTimeout(() => {
        setCopiedCommand(null);
      }, 2000);

      console.log('Command copied to clipboard (fallback):', text);
    }
  };

  if (currentPage === 'landing') {
    return <div className="terminal-exit">Returning to landing page...</div>;
  }

  return (
    <div className="terminal">
      <div className="terminal-header">
        <div className="terminal-buttons">
          <div className="terminal-button close" onClick={handleClose}>
            <span className="close-icon">×</span>
          </div>
          <div className="terminal-button minimize"></div>
          <div className="terminal-button maximize"></div>
        </div>
        <div className="terminal-title">egirls@egirls.faith-MacBook-Pro</div>
      </div>

      <div className="terminal-content" ref={outputRef}>
        {output.map((item, index) => (
          <div key={index} className={`terminal-line ${item.type}`}>
            {item.content}
          </div>
        ))}

        {!isTyping && (
          <div className="terminal-input-line">
            <span className="terminal-prompt">egirls@egirls.faith-MacBook-Pro ~ % </span>
            <input
              ref={inputRef}
              type="text"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyPress={handleKeyPress}
              className="terminal-input"
              placeholder=""
              disabled={isTyping}
            />
            <span className="terminal-cursor">|</span>
          </div>
        )}
      </div>

      {/* Command Directory */}
      <div className="command-directory">
        <div className="command-directory-header">
          <span className="command-directory-title">Commands</span>
        </div>
        <div className="command-list">
          {commandDirectory.map((command, index) => (
            <div 
              key={index} 
              className="command-item"
              onClick={() => copyToClipboard(command, index)}
              title="Click to copy"
            >
              <span className="command-text">{command}</span>
              <span className="copy-hint">
                {copiedCommand === index ? 'copied!' : 'click to copy'}
              </span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Terminal;
