import React, { useEffect, useRef, useState } from 'react';
import Terminal from './Terminal';
import './LandingPage.css';

const LandingPage = () => {
  const backgroundRef = useRef(null);
  const overlayRef = useRef(null);
  const [showTerminal, setShowTerminal] = useState(false);
  const [isTerminalClosing, setIsTerminalClosing] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      const windowHeight = window.innerHeight;
      const documentHeight = document.documentElement.scrollHeight;
      const scrollPercentage = (scrollTop / (documentHeight - windowHeight)) * 100;

      // Apply zoom effect based on scroll position
      if (backgroundRef.current) {
        const scale = 1 + (scrollTop * 0.005);
        backgroundRef.current.style.transform = `scale(${scale})`;
      }

      // Start darkening when scroll reaches 50% and gradually increase
      if (overlayRef.current) {
        if (scrollPercentage >= 50) {
          // Calculate darkening progress from 50% to 100% scroll
          const darkenProgress = (scrollPercentage - 50) / 50;
          const opacity = Math.min(darkenProgress, 1);
          overlayRef.current.style.opacity = opacity;

          // Show terminal when fade is complete
          if (opacity >= 1) {
            setShowTerminal(true);
          }
        } else {
          overlayRef.current.style.opacity = 0;
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleReturnFromTerminal = () => {
    setIsTerminalClosing(true);
    // Wait for terminal closing animation to complete
    setTimeout(() => {
      setShowTerminal(false);
      setIsTerminalClosing(false);
      // Reset scroll position to top
      window.scrollTo(0, 0);
    }, 300);
  };

  if (showTerminal) {
    return (
      <>
        <Terminal onClose={handleReturnFromTerminal} />
        {/* Show hero image immediately when terminal starts closing */}
        {isTerminalClosing && (
          <div className="closing-overlay">
            <div 
              className="closing-hero-image"
              style={{
                backgroundImage: 'url(/hero.png)',
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                backgroundRepeat: 'no-repeat'
              }}
            />
          </div>
        )}
      </>
    );
  }

  return (
    <div className="landing-page">
      <div className="background-container">
        <div 
          className="background-image" 
          ref={backgroundRef}
          style={{
            backgroundImage: 'url(/hero.png)',
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat'
          }}
        />
        <div className="black-overlay" ref={overlayRef}></div>
      </div>
    </div>
  );
};

export default LandingPage;
