import React, { useState, useEffect, useRef, useMemo } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import './Terminal.css';

// Individual Photo Component
const PhotoItem = ({ photo, index, scrollXProgress }) => {
  const photoRef = useRef(null);
  const [distanceFromCenter, setDistanceFromCenter] = useState(0);

  // Randomized base positioning and scale
  const baseScale = 0.5 + (index * 0.03) % 0.6; // 0.5-1.1 base scale
  const verticalOffset = -50 + (index * 7) % 100; // ±50px vertical offset
  const horizontalSpacing = 200 + (index * 15) % 100; // 200-300px spacing

  // Dynamic scaling based on distance from center
  const dynamicScale = useMemo(() => {
    if (distanceFromCenter === 0) return baseScale;

    const centerDistance = Math.abs(distanceFromCenter);
    const maxScale = 2.0; // Maximum scale when centered
    const minScale = 0.4; // Minimum scale when far from center

    if (centerDistance < 200) {
      // Close to center - scale up
      return Math.max(minScale, maxScale - (centerDistance / 200) * (maxScale - baseScale));
    } else {
      // Far from center - scale down
      return Math.max(minScale, baseScale - (centerDistance - 200) / 800 * (baseScale - minScale));
    }
  }, [distanceFromCenter, baseScale]);

  // Z-index based on scale (larger photos on top)
  const zIndex = Math.floor(dynamicScale * 100);

  // Update distance from center on scroll
  useEffect(() => {
    const updateDistance = () => {
      if (photoRef.current) {
        const rect = photoRef.current.getBoundingClientRect();
        const centerX = window.innerWidth / 2;
        const photoCenterX = rect.left + rect.width / 2;
        setDistanceFromCenter(photoCenterX - centerX);
      }
    };

    updateDistance();
    window.addEventListener('scroll', updateDistance);
    return () => window.removeEventListener('scroll', updateDistance);
  }, []);

  return (
    <motion.div
      ref={photoRef}
      className="photo-item"
      style={{
        scale: dynamicScale,
        translateY: verticalOffset,
        left: `${index * 250 + horizontalSpacing}px`,
        zIndex,
        position: 'absolute'
      }}
      initial={{ opacity: 0 }}
      whileInView={{ opacity: 1 }}
      viewport={{ once: true, margin: "-20%" }}
      transition={{ duration: 0.8, ease: "easeOut" }}
    >
      <img src={photo} alt={`Wine night photo ${index + 1}`} />
    </motion.div>
  );
};

