import React, { useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './LandingPage.css';

const LandingPage = () => {
  const navigate = useNavigate();
  const backgroundRef = useRef(null);
  const containerRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      const windowHeight = window.innerHeight;
      const documentHeight = document.documentElement.scrollHeight;
      const scrollPercentage = (scrollTop / (documentHeight - windowHeight)) * 100;

      // Apply zoom effect based on scroll position
      if (backgroundRef.current) {
        const scale = 1 + (scrollTop * 0.0005); // Adjust zoom intensity here
        backgroundRef.current.style.transform = `scale(${scale})`;
      }

      // Navigate to /terminal when scroll passes 80% of viewport height
      if (scrollPercentage >= 80) {
        navigate('/terminal');
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [navigate]);

  return (
    <div className="landing-page" ref={containerRef}>
      <div className="background-container">
        <div 
          className="background-image" 
          ref={backgroundRef}
          style={{
            backgroundImage: 'url(/hero.jpg)',
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat'
          }}
        />
      </div>

      <div className="content">
        <h1 className="title">Welcome to Egirls</h1>
        <p className="subtitle">Scroll to explore</p>
        <div className="scroll-indicator">
          <div className="scroll-arrow"></div>
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
