import React, { useState, useEffect, useMemo } from 'react';
import './IdentityPage.css';

const IdentityPage = () => {
  const [hoveredImage, setIsHovered] = useState(null);
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });

  const brandPhotos = [
    '/brand/EGIRLS INITIATION ENVELOPE MOCKUP.jpg',
    '/brand/EGIRLS LSD EDIT copy.jpg',
    '/brand/EGIRLS TV WALL MOCKUP.jpg',
    '/brand/EVERYTHING WILL KILL YOU POSTER (1).jpg',
    '/brand/STAGE 2 EGIRLS.jpg',
    '/brand/TRAFFIC LIGHT.jpg',
    '/brand/WhatsApp Image 2025-06-04 at 14.35.32 (3).jpeg',
    '/brand/WhatsApp Image 2025-06-04 at 14.35.32 (4).jpeg',
    '/brand/WhatsApp Image 2025-06-04 at 14.35.32 (5).jpeg',
    '/brand/WhatsApp Image 2025-06-05 at 22.42.36.jpeg',
    '/brand/WhatsApp Image 2025-06-10 at 18.02.52 (1).jpeg',
    '/brand/WhatsApp Image 2025-06-11 at 13.10.45 (1).jpeg',
    '/brand/WhatsApp Image 2025-06-11 at 14.23.40 (3).jpeg',
    '/brand/WhatsApp Image 2025-06-11 at 14.23.41 (1).jpeg',
    '/brand/WhatsApp Image 2025-06-12 at 23.17.49 (2).jpeg',
    '/brand/WhatsApp Image 2025-06-12 at 23.17.50 (1).jpeg',
    '/brand/WhatsApp Image 2025-06-12 at 23.17.50 (2).jpeg',
    '/brand/WhatsApp Image 2025-08-20 at 15.04.27.jpeg',
    '/brand/billboard.jpg',
    '/brand/cd-case.jpg',
    '/brand/floppy-disk.jpg',
    '/brand/window-poster.jpg'
  ];

  useEffect(() => {
    const handleResize = () => {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const imageStyles = useMemo(() => {
    const isMobile = windowSize.width <= 768;
    const isSmallMobile = windowSize.width <= 480;
    const isTinyMobile = windowSize.width <= 360;

    const imageWidth = isTinyMobile ? 80 : isSmallMobile ? 100 : isMobile ? 120 : 150;
    const imageHeight = isTinyMobile ? 60 : isSmallMobile ? 75 : isMobile ? 90 : 112;
    const padding = isMobile ? 10 : 20;
    const minSpacing = isMobile ? 15 : 30;
    const headerHeight = 60;
    const footerHeight = 40;
    const safeHeight = windowSize.height - headerHeight - footerHeight - (padding * 2);
    const availableWidth = windowSize.width - (padding * 2);
    const availableHeight = safeHeight - (padding * 2);

    return brandPhotos.map((photo, index) => {
      // For mobile, distribute images across 4 sections
      let x, y;

      if (isMobile) {
        const section = index % 4;
        const sectionWidth = availableWidth / 4;
        const sectionHeight = availableHeight / 2;

        if (section < 2) {
          // Top row
          x = (section * sectionWidth) + (sectionWidth / 2) - (imageWidth / 2);
          y = (availableHeight / 4) - (imageHeight / 2);
        } else {
          // Bottom row
          x = ((section - 2) * sectionWidth) + (sectionWidth / 2) - (imageWidth / 2);
          y = (availableHeight * 3 / 4) - (imageHeight / 2);
        }
      } else {
        // Desktop: random distribution with collision detection
        const maxAttempts = 100;
        let attempts = 0;

        do {
          x = Math.random() * (availableWidth - imageWidth);
          y = Math.random() * (availableHeight - imageHeight);
          attempts++;
        } while (attempts < maxAttempts);
      }

      // Clamp to viewport bounds
      x = Math.max(padding, Math.min(x, availableWidth - imageWidth - padding));
      y = Math.max(padding, Math.min(y, availableHeight - imageHeight - padding));

      return {
        x,
        y,
        width: imageWidth,
        height: imageHeight,
        transform: `translate(${x}px, ${y}px)`
      };
    });
  }, [brandPhotos, windowSize]);

  return (
    <div className="identity-page">
      <div className="identity-header">
        <div className="macos-window-controls">
          <div className="control red"></div>
          <div className="control yellow"></div>
          <div className="control green"></div>
        </div>
        <div className="macos-title">Identity - egirls.faith</div>
      </div>

      <div className="identity-collage-wrapper">
        {brandPhotos.map((photo, index) => (
          <div
            key={index}
            className="collage-image"
            style={imageStyles[index]}
            onMouseEnter={() => setIsHovered(index)}
            onMouseLeave={() => setIsHovered(null)}
          >
            <img
              src={encodeURI(photo)}
              alt={`Brand asset ${index + 1}`}
              onLoad={() => console.log(`Image ${index + 1} loaded successfully`)}
              onError={(e) => console.error(`Failed to load image ${index + 1}:`, e)}
            />
          </div>
        ))}
      </div>

      <div className="identity-footer">
        <div className="identity-logo">
          <img src="/egirls-logo.png" alt="Egirls Logo" />
        </div>
      </div>
    </div>
  );
};

export default IdentityPage;
