import React, { useEffect, useRef } from 'react';
import './LandingPage.css';

const LandingPage = () => {
  const backgroundRef = useRef(null);
  const overlayRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      const windowHeight = window.innerHeight;
      const documentHeight = document.documentElement.scrollHeight;
      const scrollPercentage = (scrollTop / (documentHeight - windowHeight)) * 100;

      // Apply zoom effect based on scroll position - allow much more zooming
      if (backgroundRef.current) {
        const scale = 1 + (scrollTop * 0.005); // Increased zoom intensity even more
        backgroundRef.current.style.transform = `scale(${scale})`;
      }

      // Start darkening when zoom reaches 50% and gradually increase throughout the zoom
      if (overlayRef.current) {
        // Calculate zoom percentage based on scale
        const currentScale = 1 + (scrollTop * 0.005);
        const zoomPercentage = ((currentScale - 1) / 0.5) * 100; // Normalize to percentage

        // Start darkening at 50% zoom and gradually increase
        if (zoomPercentage >= 50) {
          const darkenProgress = (zoomPercentage - 50) / 50; // Progress from 50% to 100% zoom
          const easedProgress = 1 - Math.pow(1 - darkenProgress, 2); // Quadratic ease-out for smoother transition
          const opacity = Math.min(easedProgress, 1);
          overlayRef.current.style.opacity = opacity;
        } else {
          overlayRef.current.style.opacity = 0;
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="landing-page">
      <div className="background-container">
        <div 
          className="background-image" 
          ref={backgroundRef}
          style={{
            backgroundImage: 'url(/hero.png)',
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat'
          }}
        />
        <div className="black-overlay" ref={overlayRef}></div>
      </div>
    </div>
  );
};

export default LandingPage;
